import React, { Component } from 'react';
import { RoomContext } from '../context';

export default class extends Component {
	static contextType = RoomContext;
	render() {
		const { name, greeting } = this.context;
		return (
			<div>
				<div>
					{greeting} {name} from featured rooms
				</div>
			</div>
		);
	}
}
